const express = require('express');
const app = express();

// Middleware

const middleware =(req, res, next)=>{
    console.log(`Hello Middleware`);
    next();
}

// middleware();

app.get('/', (req, res)=>{
    res.send(`Hello world from my first server`);
});

app.get('/about', middleware, (req, res)=>{
    console.log('HEllo my about')
    res.send(`Hello about from my first server`);
});

app.get('/signin', (req, res)=>{
    res.send(`Hello Signin from my first server`);
});

app.get('/signup', (req, res)=>{
    res.send(`Hello Signup from my first server`);
});

app.listen(8080, ()=>{
    console.log(`Server is running on Port no 8080`);
})
